  var days=[];
  var price=[];
  var bidder=[];
for (x=0; x< bids.length; x++)
{
  var d=new Date(bids[x].created_at);
  days.push(d.toDateString());
  price.push(bids[x].bid_price);
  bidder.push(get_user_name(bids[x].bidder_id));
}
